const ContactList = ({ contacts, delContact }) => {
  const deletedItem = prevState.contacts.filter(contact => contact.id === id);
  const itemsArr = JSON.parse(localStorage.getItem('deletedContacts')) || [];
  itemsArr.push(deletedItem);
  localStorage.setItem('deletedContacts', JSON.stringify(itemsArr));
  return (
    <ul>
      {contacts.map(contact => (
        <li key={contact.id}>
          {contact.name} - {contact.number}
          <button type="button" onClick={() => delContact(contact.id)}>
            delete
          </button>
        </li>
      ))}
    </ul>
  );
};

export default ContactList;
